package javacoreprograms;

public class Prob1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k=1;
		for(int i=1;i<=4-i;i++)
		{
			for(int j=0;j<=4;j++)
			{
				System.out.print(k);
				System.out.print("\t");//\t is for space
				k++;
			}
			System.out.println( "");
		}

	}

}
